import {BsFillBagPlusFill} from "react-icons/bs"
import React, { useState } from "react";
import "../Allcss/Header.css";
import {Outlet,Link} from 'react-router-dom';
function Header() {

     const [showMediaIcons,setshowMediaIcons]=useState(false);
     const [active , setActive] = useState(" ");
     


    return (
        <div>
            <header class="header">
                <Link to="" alt="logo" class="logo">
                    Gangu Shop
                </Link>
                <ul className={ showMediaIcons ? "menu show-menu": "menu"}>
                    <li ><Link to="/" key={1} onClick={()=>setActive("")} className={active ? "active" :" "} >home</Link></li>
                    <li><Link to="/shop" key={2} onClick={()=>setActive("active")} className={active ? "active" :" "}>shop</Link></li>
                    <li><Link to="/Blog" key={3} onClick={()=>setActive("active")} className={active ? "active" :" "}>blog</Link></li>
                    <li><Link to="/about" key={4} onClick={()=>setActive("active")} className={active ? "active" :" "}>about</Link></li>
                    <li><Link to="/contact" key={1} onClick={()=>setActive("active")} className={active ? "active" :" "}>contact</Link></li>
                    <li><Link to="/cart" key={1} onClick={()=>setActive("active")} className={active ? "active" :" "}>
                        <BsFillBagPlusFill/></Link></li>
                </ul>
                <span className="menu-icon" onClick={()=>setshowMediaIcons(!showMediaIcons)}><i class="fa-solid fa-bars"></i></span>
                <Outlet/>
            </header>

        </div>
    )
}
export default Header;
