import React from 'react'
import "../css/style.css"
const ShopHeader = () => {
  return (
    <div>
        <section class="shop-header">
            <h1>#stayhome</h1>
            <p>Save more with coupons & upto 70% off!</p>
        </section>
    </div>
  )
}

export default ShopHeader;
